import React from 'react';
import './MovieCard.css';
import defaultPoster from '../assets/default-poster.jpg'


class MovieCard extends React.Component {

	castSearch = async () => {
		let stars = await fetch(`https://api.themoviedb.org/3/movie/${this.props.key}/credits?api_key=701fb55bd48bf310eeff75b536e6ff72`);
		stars = await stars.json();
		stars = stars.cast.name.splice(0,4).join(', ');
		return stars;
	}

	render() {
		return  (
			<div className="item">
				<div className="ui grid">
					<div className="ui row">
						<div className="three wide column">
							<img 
								className="ui image"
								src={this.props.poster ? `http://image.tmdb.org/t/p/original/${this.props.poster}` : defaultPoster }
								alt={this.props.title}
							/>
						</div>
						<div className="thirteen wide column">
							<div className="content">
								<h2 className="header">{this.props.title}</h2>
								<p className="description">{this.props.synopsis}</p>
								<p>{this.castSearch()}</p>
							</div>
						</div>
					</div>
				</div>
			</div>
		);
	};
}


