import React from 'react';
import './MovieCard.css';
import defaultPoster from '../assets/default-poster.jpg'


class MovieCard extends React.Component {

	state = {stars: ''}

	componentDidMount() {
		this.castSearch(this.props.id);
	}

	castSearch = async id => {
		let test = await fetch(`https://api.themoviedb.org/3/movie/${id}/credits?api_key=701fb55bd48bf310eeff75b536e6ff72`);
		test = await test.json();
		test = test.cast.slice(0,4);
		const stars = test.map(obj => obj.name).join(', ');
		this.setState({stars: stars})
	}


	render() {
		return  (
			<div className="item">
				<div className="ui grid">
					<div className="ui row">
						<div className="three wide column">
							<img 
								className="ui image"
								src={this.props.poster ? `http://image.tmdb.org/t/p/original/${this.props.poster}` : defaultPoster }
								alt={this.props.title}
							/>
						</div>
						<div className="thirteen wide column">
							<div className="content">
								<h2 className="header">{this.props.title} ({this.props.year})}</h2>
								<p className="description">{this.props.synopsis}</p>
								<p className="description">Starring: {this.state.stars}</p>
							</div>
						</div>
					</div>
				</div>
			</div>
		);
	};
}

